import Link from 'next/link'
import Head from 'next/head'

export default class Layout extends React.Component {
  render() {
    const { children, title } = this.props

    return (
      <div>
        <Head>
          <meta name="viewport" content="width=device-width, initial-scale=1" />
          <title>{title}</title>
        </Head>
        <header>
          <Link href="/">
            <a>
              Podcasts
            </a>
          </Link>
        </header>

        {children}

        <style jsx>{`
          header {
            color: #fff;
            background: #8756ca;
            padding: 15px;
            text-align: center;
          }

          header aÂ {
            color: #fff;
          }
        `}</style>

        <style jsx global>{`
          body {
            background: white;
          }


        `}</style>
      </div>
    )
  }
}
